function calculateResults(answers, questions) {
  const sections = { '–°—Ç—Ä–∞—Ç–µ–≥–∏—è': { blocks: [], totalGreen: 0, totalRed: 0 }, '–õ–∏–¥–≥–µ–Ω': { blocks: [], totalGreen: 0, totalRed: 0 }, '–ü—Ä–æ–¥–∞–∂–∏': { blocks: [], totalGreen: 0, totalRed: 0 } };
  questions.filter(q => !q.isQualification).forEach(q => {
    const isGreen = answers[q.id] === true;
    sections[q.section].blocks.push({ id: q.id, title: q.title, level: q.level, status: isGreen ? 'success' : 'error', recommendation: isGreen ? q.yes_rec : q.no_rec });
    if (isGreen) sections[q.section].totalGreen++; else sections[q.section].totalRed++;
  });
  Object.keys(sections).forEach(s => sections[s].blocks.sort((a, b) => a.level - b.level));
  return { sections };
}

