function QualificationQuestionScreen({ question, questionNumber, totalQuestions, onAnswer, onBack, canGoBack }) {
  const [textAnswer, setTextAnswer] = useState('');
  const [selectedOptions, setSelectedOptions] = useState([]);
  const [customOption, setCustomOption] = useState('');
  const handleSubmit = () => { if (question.type === 'text') onAnswer(textAnswer); else if (question.type === 'select') onAnswer(selectedOptions[0] || ''); else if (question.type === 'multiselect') { const finalAnswer = [...selectedOptions]; if (customOption.trim()) finalAnswer.push(customOption.trim()); onAnswer(finalAnswer); } };
  const toggleOption = (option) => { if (question.type === 'select') setSelectedOptions([option]); else { if (selectedOptions.includes(option)) setSelectedOptions(selectedOptions.filter(o => o !== option)); else if (selectedOptions.length < 3) setSelectedOptions([...selectedOptions, option]); } };
  const canSubmit = () => { if (question.type === 'text') return textAnswer.trim().length > 0; if (question.type === 'select') return selectedOptions.length === 1; if (question.type === 'multiselect') return selectedOptions.length > 0 || customOption.trim().length > 0; return false; };
  return (<div style={{ minHeight: '100vh', display: 'flex', flexDirection: 'column', background: '#0a0a0a' }}><div style={{ padding: '24px', maxWidth: '800px', margin: '0 auto', width: '100%' }}><GamifiedProgressBar currentQuestion={questionNumber} totalQuestions={totalQuestions} /><MotivationalMessage questionNumber={questionNumber} /><div style={{ fontFamily: "'JetBrains Mono', monospace", fontSize: '11px', color: '#4299e1', letterSpacing: '1px', marginBottom: '8px' }}>–ö–í–ê–õ–ò–§–ò–ö–ê–¶–ò–û–ù–ù–´–ô –í–û–ü–†–û–°</div><h2 style={{ fontSize: '24px', fontWeight: 700, marginBottom: '32px', lineHeight: 1.4 }}>{question.question}</h2>{question.type === 'text' && (<textarea value={textAnswer} onChange={(e) => setTextAnswer(e.target.value)} placeholder={question.placeholder} style={{ width: '100%', minHeight: '120px', padding: '16px 20px', fontSize: '16px', background: 'rgba(255,255,255,0.05)', border: '1px solid rgba(255,255,255,0.1)', borderRadius: '12px', color: '#fff', outline: 'none', fontFamily: 'inherit', resize: 'vertical', marginBottom: '24px' }} />)}{(question.type === 'select' || question.type === 'multiselect') && (<div style={{ display: 'flex', flexDirection: 'column', gap: '12px', marginBottom: '24px' }}>{question.options.map((option, idx) => (<button key={idx} onClick={() => toggleOption(option)} style={{ padding: '16px 20px', fontSize: '15px', background: selectedOptions.includes(option) ? 'rgba(66, 153, 225, 0.2)' : 'rgba(255,255,255,0.05)', border: `2px solid ${selectedOptions.includes(option) ? '#4299e1' : 'rgba(255,255,255,0.1)'}`, borderRadius: '12px', color: '#fff', cursor: 'pointer', textAlign: 'left' }}>{option}</button>))}{question.allowCustom && (<input type="text" value={customOption} onChange={(e) => setCustomOption(e.target.value)} placeholder="–ù–∞–ø–∏—à–∏—Ç–µ —Å–≤–æ–π –≤–∞—Ä–∏–∞–Ω—Ç..." style={{ padding: '16px 20px', fontSize: '15px', background: 'rgba(255,255,255,0.05)', border: '1px solid rgba(255,255,255,0.1)', borderRadius: '12px', color: '#fff', outline: 'none', fontFamily: 'inherit' }} />)}{question.type === 'multiselect' && <p style={{ fontSize: '13px', color: '#666' }}>–í—ã–±—Ä–∞–Ω–æ: {selectedOptions.length}/3</p>}</div>)}<button onClick={handleSubmit} disabled={!canSubmit()} style={{ width: '100%', padding: '18px', fontSize: '16px', fontWeight: 600, background: canSubmit() ? '#4299e1' : '#333', color: '#fff', border: 'none', borderRadius: '12px', cursor: canSubmit() ? 'pointer' : 'not-allowed', marginBottom: '16px' }}>–ü—Ä–æ–¥–æ–ª–∂–∏—Ç—å ‚Üí</button>{canGoBack && <button onClick={onBack} style={{ background: 'transparent', border: 'none', color: '#666', fontSize: '14px', cursor: 'pointer', padding: '8px' }}>‚Üê –ù–∞–∑–∞–¥</button>}</div></div>);
}

function ResultsScreen({ results, onRestart }) {
  const [selectedBlock, setSelectedBlock] = useState(null);
  const sections = ['–°—Ç—Ä–∞—Ç–µ–≥–∏—è', '–õ–∏–¥–≥–µ–Ω', '–ü—Ä–æ–¥–∞–∂–∏'];
  let totalGreen = 0, totalRed = 0;
  sections.forEach(s => { totalGreen += results.sections[s].totalGreen; totalRed += results.sections[s].totalRed; });
  const totalPercent = Math.round((totalGreen / (totalGreen + totalRed)) * 100);
  return (<div style={{ minHeight: '100vh', background: '#f5f5f5' }}><header style={{ padding: '20px 24px', background: '#fff', borderBottom: '1px solid #e5e5e5', display: 'flex', alignItems: 'center', justifyContent: 'space-between', flexWrap: 'wrap', gap: '16px' }}><div><div style={{ fontFamily: "'JetBrains Mono', monospace", fontSize: '11px', color: '#888', letterSpacing: '1px', marginBottom: '4px' }}>–†–ï–ó–£–õ–¨–¢–ê–¢–´ –ê–£–î–ò–¢–ê</div><h1 style={{ fontSize: '20px', fontWeight: 700, color: '#333' }}>–ú–∞—Ä–∫–µ—Ç–∏–Ω–≥-–ê—É–¥–∏—Ç ¬∑ Method Dobrusin</h1></div><button onClick={onRestart} style={{ padding: '10px 20px', background: '#4299e1', color: '#fff', border: 'none', borderRadius: '8px', cursor: 'pointer', fontSize: '13px', fontWeight: 600 }}>–ü—Ä–æ–π—Ç–∏ –∑–∞–Ω–æ–≤–æ</button></header><main style={{ padding: '24px', maxWidth: '1200px', margin: '0 auto' }}><div style={{ background: '#fff', borderRadius: '12px', padding: '24px', marginBottom: '24px', textAlign: 'center' }}><div style={{ fontSize: '48px', fontWeight: 800, color: totalPercent >= 70 ? '#22c55e' : totalPercent >= 40 ? '#eab308' : '#ef4444', marginBottom: '8px' }}>{totalPercent}%</div><p style={{ color: '#666', fontSize: '14px' }}>–û–±—â–∏–π —Ä–µ–∑—É–ª—å—Ç–∞—Ç –¥–∏–∞–≥–Ω–æ—Å—Ç–∏–∫–∏</p><p style={{ color: '#888', fontSize: '13px', marginTop: '8px' }}>‚úÖ {totalGreen} –≤ –∑–µ–ª—ë–Ω–æ–π –∑–æ–Ω–µ ‚Ä¢ ‚ùå {totalRed} —Ç—Ä–µ–±—É—é—Ç –≤–Ω–∏–º–∞–Ω–∏—è</p></div>{sections.map(sectionName => { const section = results.sections[sectionName]; const sectionPercent = Math.round((section.totalGreen / section.blocks.length) * 100); return (<div key={sectionName} style={{ background: '#fff', borderRadius: '12px', padding: '20px', marginBottom: '16px' }}><div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '16px' }}><h3 style={{ fontSize: '18px', fontWeight: 700, color: '#333' }}>{sectionName}</h3><span style={{ fontSize: '14px', fontWeight: 600, color: sectionPercent >= 70 ? '#22c55e' : sectionPercent >= 40 ? '#eab308' : '#ef4444' }}>{sectionPercent}%</span></div><div style={{ display: 'flex', flexDirection: 'column', gap: '8px' }}>{section.blocks.map(block => (<div key={block.id}><div onClick={() => setSelectedBlock(selectedBlock?.id === block.id ? null : block)} style={{ display: 'flex', alignItems: 'center', gap: '12px', padding: '12px 16px', background: block.status === 'success' ? 'rgba(34, 197, 94, 0.1)' : 'rgba(239, 68, 68, 0.1)', borderRadius: '8px', cursor: 'pointer' }}><div style={{ width: '24px', height: '24px', borderRadius: '50%', background: block.status === 'success' ? '#22c55e' : '#ef4444', display: 'flex', alignItems: 'center', justifyContent: 'center', fontSize: '12px', color: '#fff' }}>{block.status === 'success' ? '‚úì' : '‚úó'}</div><span style={{ flex: 1, fontSize: '14px', color: '#333' }}>{block.title}</span><span style={{ fontSize: '12px', color: '#888' }}>–£—Ä.{block.level}</span></div>{selectedBlock?.id === block.id && <div style={{ padding: '16px', background: '#f9f9f9', borderRadius: '0 0 8px 8px', marginTop: '-4px', fontSize: '14px', color: '#555', lineHeight: 1.6 }}>{block.recommendation}</div>}</div>))}</div></div>); })}</main></div>);
}

function ContactScreen({ onSubmit, onSkip }) {
  const [name, setName] = useState('');
  const [phone, setPhone] = useState('');
  const [email, setEmail] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);
  const handleSubmit = async () => { setIsSubmitting(true); await onSubmit({ name, phone, email }); setIsSubmitting(false); };
  return (<div style={{ minHeight: '100vh', display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', padding: '40px 20px', background: 'linear-gradient(180deg, #0a0a0a 0%, #111 100%)' }}><div style={{ maxWidth: '480px', width: '100%', textAlign: 'center' }}><div style={{ fontSize: '48px', marginBottom: '24px' }}>üèÜ</div><h1 style={{ fontSize: '28px', fontWeight: 800, color: '#fff', marginBottom: '12px' }}>–í—ã –¥–æ—Å—Ç–∏–≥–ª–∏ —Ñ–∏–Ω–∏—à–∞!</h1><p style={{ fontSize: '16px', color: '#888', marginBottom: '32px', lineHeight: 1.6 }}>–û—Å—Ç–∞–≤—å—Ç–µ –∫–æ–Ω—Ç–∞–∫—Ç—ã, —á—Ç–æ–±—ã –ø–æ–ª—É—á–∏—Ç—å –ø–µ—Ä—Å–æ–Ω–∞–ª—å–Ω—ã–µ —Ä–µ–∫–æ–º–µ–Ω–¥–∞—Ü–∏–∏</p><div style={{ display: 'flex', flexDirection: 'column', gap: '16px', marginBottom: '24px' }}><input type="text" placeholder="–í–∞—à–µ –∏–º—è" value={name} onChange={(e) => setName(e.target.value)} style={{ width: '100%', padding: '16px 20px', fontSize: '16px', background: 'rgba(255,255,255,0.05)', border: '1px solid rgba(255,255,255,0.1)', borderRadius: '12px', color: '#fff', outline: 'none', fontFamily: 'inherit', boxSizing: 'border-box' }} /><input type="tel" placeholder="–¢–µ–ª–µ—Ñ–æ–Ω" value={phone} onChange={(e) => setPhone(e.target.value)} style={{ width: '100%', padding: '16px 20px', fontSize: '16px', background: 'rgba(255,255,255,0.05)', border: '1px solid rgba(255,255,255,0.1)', borderRadius: '12px', color: '#fff', outline: 'none', fontFamily: 'inherit', boxSizing: 'border-box' }} /><input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} style={{ width: '100%', padding: '16px 20px', fontSize: '16px', background: 'rgba(255,255,255,0.05)', border: '1px solid rgba(255,255,255,0.1)', borderRadius: '12px', color: '#fff', outline: 'none', fontFamily: 'inherit', boxSizing: 'border-box' }} /></div><button onClick={handleSubmit} disabled={isSubmitting || (!name && !phone && !email)} style={{ width: '100%', padding: '18px 32px', fontSize: '16px', fontWeight: 600, background: (isSubmitting || (!name && !phone && !email)) ? '#333' : '#22c55e', color: '#fff', border: 'none', borderRadius: '12px', cursor: (isSubmitting || (!name && !phone && !email)) ? 'not-allowed' : 'pointer', fontFamily: 'inherit', marginBottom: '16px' }}>{isSubmitting ? '–û—Ç–ø—Ä–∞–≤–∫–∞...' : '–ü–æ–ª—É—á–∏—Ç—å —Ä–µ–∑—É–ª—å—Ç–∞—Ç—ã ‚Üí'}</button><button onClick={onSkip} style={{ background: 'transparent', border: 'none', color: '#666', fontSize: '14px', cursor: 'pointer', fontFamily: 'inherit', padding: '8px' }}>–ü—Ä–æ–ø—É—Å—Ç–∏—Ç—å –∏ –ø–æ—Å–º–æ—Ç—Ä–µ—Ç—å —Ä–µ–∑—É–ª—å—Ç–∞—Ç—ã</button></div></div>);
}

async function sendToTelegram(contactData, answers, results) {
  const { name, phone, email } = contactData;
  const sections = ['–°—Ç—Ä–∞—Ç–µ–≥–∏—è', '–õ–∏–¥–≥–µ–Ω', '–ü—Ä–æ–¥–∞–∂–∏'];
  let statsText = '', totalGreen = 0, totalRed = 0;
  sections.forEach(section => { const s = results.sections[section]; totalGreen += s.totalGreen; totalRed += s.totalRed; statsText += `\nüìä ${section}: ${Math.round((s.totalGreen / s.blocks.length) * 100)}% (‚úÖ${s.totalGreen} / ‚ùå${s.totalRed})`; });
  const totalPercent = Math.round((totalGreen / (totalGreen + totalRed)) * 100);
  const redBlocks = []; sections.forEach(section => results.sections[section].blocks.filter(b => b.status === 'error').forEach(b => redBlocks.push(`‚Ä¢ ${b.title} (${section})`)));
  const qualAnswers = [];
  if (answers['–ö1']) qualAnswers.push(`–ë–∏–∑–Ω–µ—Å: ${answers['–ö1']}`);
  if (answers['–ö2']) qualAnswers.push(`–ü—Ä–æ–±–ª–µ–º—ã: ${Array.isArray(answers['–ö2']) ? answers['–ö2'].join(', ') : answers['–ö2']}`);
  if (answers['–ö3']) qualAnswers.push(`–û–±–æ—Ä–æ—Ç: ${answers['–ö3']}`);
  const message = `üéØ *–ù–æ–≤—ã–π —Ä–µ–∑—É–ª—å—Ç–∞—Ç –∞—É–¥–∏—Ç–∞!*\n\nüë§ *–ö–æ–Ω—Ç–∞–∫—Ç:*\n–ò–º—è: ${name || '–ù–µ —É–∫–∞–∑–∞–Ω–æ'}\n–¢–µ–ª–µ—Ñ–æ–Ω: ${phone || '–ù–µ —É–∫–∞–∑–∞–Ω'}\nEmail: ${email || '–ù–µ —É–∫–∞–∑–∞–Ω'}\n\nüìã *–ö–≤–∞–ª–∏—Ñ–∏–∫–∞—Ü–∏—è:*\n${qualAnswers.join('\n')}\n\nüìà *–û–±—â–∏–π —Ä–µ–∑—É–ª—å—Ç–∞—Ç: ${totalPercent}%*${statsText}\n\n${redBlocks.length > 0 ? `‚ùå *–ü—Ä–æ–±–ª–µ–º–Ω—ã–µ –∑–æ–Ω—ã (${redBlocks.length}):*\n${redBlocks.slice(0, 10).join('\n')}` : '‚úÖ *–í—Å–µ –±–ª–æ–∫–∏ –≤ –∑–µ–ª—ë–Ω–æ–π –∑–æ–Ω–µ!*'}\n\n‚è∞ ${new Date().toLocaleString('ru-RU')}`;
  try { await fetch(`https://api.telegram.org/bot${TELEGRAM_CONFIG.botToken}/sendMessage`, { method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify({ chat_id: TELEGRAM_CONFIG.chatId, text: message, parse_mode: 'Markdown' }) }); } catch (error) { console.error('Telegram error:', error); }
}

export default function MarketingAudit() {
  const [screen, setScreen] = useState('start');
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [answers, setAnswers] = useState({});
  const [results, setResults] = useState(null);
  const [showCheckpoint, setShowCheckpoint] = useState(null);
  const currentQuestion = allQuestions[currentQuestionIndex];
  const totalQuestions = allQuestions.length;
  const handleStart = () => { setScreen('testing'); setCurrentQuestionIndex(0); setAnswers({}); };
  const handleAnswer = (answer) => { const newAnswers = { ...answers, [currentQuestion.id]: answer }; setAnswers(newAnswers); const nextIndex = currentQuestionIndex + 1; const currentQNum = currentQuestion.questionNumber; const reachedCheckpoint = checkpoints.find(c => c.question === currentQNum && currentQNum > 1); if (reachedCheckpoint) setShowCheckpoint(reachedCheckpoint); else if (nextIndex < totalQuestions) setCurrentQuestionIndex(nextIndex); else { setResults(calculateResults(newAnswers, allQuestions)); setScreen('contact'); } };
  const handleCheckpointContinue = () => { setShowCheckpoint(null); const nextIndex = currentQuestionIndex + 1; if (nextIndex < totalQuestions) setCurrentQuestionIndex(nextIndex); else { setResults(calculateResults(answers, allQuestions)); setScreen('contact'); } };
  const handleBack = () => { if (currentQuestionIndex > 0) setCurrentQuestionIndex(currentQuestionIndex - 1); };
  const handleContactSubmit = async (contactData) => { await sendToTelegram(contactData, answers, results); setScreen('results'); };
  const handleContactSkip = () => setScreen('results');
  const handleRestart = () => { setScreen('start'); setCurrentQuestionIndex(0); setAnswers({}); setResults(null); setShowCheckpoint(null); };
  return (<><style>{globalStyles}</style>{showCheckpoint && <CheckpointAchieved checkpoint={showCheckpoint} onContinue={handleCheckpointContinue} />}{screen === 'start' && <StartScreen onStart={handleStart} />}{screen === 'testing' && currentQuestion && (currentQuestion.isQualification ? <QualificationQuestionScreen question={currentQuestion} questionNumber={currentQuestion.questionNumber} totalQuestions={totalQuestions} onAnswer={handleAnswer} onBack={handleBack} canGoBack={currentQuestionIndex > 0} /> : <DiagnosticQuestionScreen question={currentQuestion} questionNumber={currentQuestion.questionNumber} totalQuestions={totalQuestions} onAnswer={handleAnswer} onBack={handleBack} canGoBack={currentQuestionIndex > 0} />)}{screen === 'contact' && results && <ContactScreen onSubmit={handleContactSubmit} onSkip={handleContactSkip} />}{screen === 'results' && results && <ResultsScreen results={results} onRestart={handleRestart} />}</>);
}
